	RCC_AHB2ENR_ptr->GPIOAEN = PIN_STATE_HIGH;

	//Set ROWS to OUTPUT and COLS to INPUT
	GPIOA_MODER_ptr->pin_2 = PIN_MODE_OUTPUT;
	GPIOA_MODER_ptr->pin_7 = PIN_MODE_OUTPUT;
