	GPIOA_MODER_ptr->pin_0 = PIN_MODE_INPUT;

	//Enable pull-up resistor for COLS
	GPIOA_PUPDR_ptr->pin_4 = PIN_PULL_UP_EN;
	GPIOA_PUPDR_ptr->pin_3 = PIN_PULL_UP_EN;
	GPIOA_PUPDR_ptr->pin_1 = PIN_PULL_UP_EN;
